import { useState, useEffect } from 'react';
import axios from 'axios';

const ProductsApi = () => {
	const [products, setProducts] = useState([]);
	useEffect(() => {
		getProducts();
	}, []);
	return {
		products: [products, setProducts]
	};
};

export default ProductsApi;
