//Get product has been in cart  => display

import React, { useContext, useState, useEffect } from 'react';
import { GlobalState } from '../../../GlobalState';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Cart = () => {
	const state = useContext(GlobalState);
	const [cart, setCart] = state.userAPI.cart;
	const [total, setTotal] = useState(0);
	const [token] = state.token;

	useEffect(() => {
		const getTotal = () => {
			const total = cart.reduce((prev, item) => {
				return prev + item.price * item.quantity;
			}, 0);
			setTotal(total);
		};
		getTotal();
	}, [cart]);
	const addToCart = async () => {
		await axios.patch(
			'/user/addcart',
			{ cart },
			{
				headers: { Authorization: token }
			}
		);
	};

	const increase = (id) => {
		cart.forEach((item) => {
			if (item._id === id) {
				item.quantity += 1;
			}
		});
		//New cart
		setCart([...cart]);
		addToCart();
	};

	const decrease = (id) => {
		cart.forEach((item) => {
			if (item._id === id) {
				item.quantity === 1 ? (item.quantity = 1) : (item.quantity -= 1);
			}
		});
		//New cart
		setCart([...cart]);
		addToCart();
	};

	const removeProduct = (id) => {
		if (window.confirm('Do you to delete this product ?')) {
			cart.forEach((item, index) => {
				if (item._id === id) {
					cart.splice(index, 1);
				}
			});

			setCart([...cart]);
			addToCart();
		}
	};

	if (cart.length === 0)
		return (
			<h2 style={{ textAlign: 'center', fontSize: '5rem' }}>CART EMPTY</h2>
		);

	return (
		<div>
			{cart.map((product) => (
				<div className='detail cart' key={product._id}>
					<img src={product.images.url} alt='#'></img>
					<div className='box-detail'>
						<h2>{product.title}</h2>
						<h3>$ {product.price * product.quantity}</h3>
						<p>{product.description}</p>
						<p>{product.content}</p>

						<div className='amount'>
							<button onClick={() => decrease(product._id)}>-</button>
							<span>{product.quantity}</span>
							<button onClick={() => increase(product._id)}>+</button>
						</div>

						<div className='delete' onClick={() => removeProduct(product._id)}>
							X
						</div>
					</div>
				</div>
			))}

			<div className='total'>
				<h3>Total : $ {total}</h3>
				<Link to='#!'>Payment</Link>
			</div>
		</div>
	);
};

export default Cart;
